INSERT INTO `routes` VALUES (11, '伊犁环线7天6晚这样玩❗', 7, '新疆伊犁7日游，快带你的小伙伴一起来玩！', '<p><span style=\"color: rgb(96, 98, 102); background-color: rgb(255, 255, 255); font-size: 14px;\">这个季节一定要去北疆❗ 去看蓝色湖泊💦、郁郁葱葱的草原🌿、漫山遍野的花海🌸、随手一拍就是大片📸 🌈路线参考： Day1：抵达乌鲁木齐--自由活动--住乌鲁木齐 Day2：乌鲁木齐--赛里木湖--赛里木湖帆船--住赛里木湖 Day3：赛里木湖日出+环湖自驾--果子沟大桥--琼库什台村--住琼库什台 Day4：琼库什台--恰西草原--住巩留/新源 Day5：巩留/新源--那拉提草原--住那拉提 Day6：那拉提--独库公路--独库公路下午茶--住乌鲁木齐 Day7：乌鲁木齐--返程</span></p>', 'http://localhost:8080/files/download/1732221623312-lx1.webp', NULL, NULL, NULL);
